import React, { useState, useEffect } from 'react';
import { useMutation } from '@apollo/client';
import { CREATE_USER, UPDATE_USER } from '../graphql/mutations';

const UserForm = ({ user, refetchUsers }) => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [role, setRole] = useState('');

  useEffect(() => {
    if (user) {
      setUsername(user.username);
      setEmail(user.email);
      setRole(user.role);
    } else {
      setUsername('');
      setEmail('');
      setRole('');
    }
  }, [user]);

  const [createUser, { loading: creating }] = useMutation(CREATE_USER, {
    onCompleted: () => {
      console.log('User created successfully');
      setUsername('');
      setEmail('');
      setRole('');
      refetchUsers();
    },
    onError: (error) => {
      console.error('Error creating user:', error.message);
    },
  });

  const [updateUser, { loading: updating }] = useMutation(UPDATE_USER, {
    onCompleted: () => {
      console.log('User updated successfully');
      refetchUsers();
    },
    onError: (error) => {
      console.error('Error updating user:', error.message);
    },
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (user) {
      updateUser({ variables: { id: user.id, input: { username, email, role } } });
    } else {
      createUser({ variables: { username, email, role } });
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Username" />
      <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" />
      <input type="text" value={role} onChange={(e) => setRole(e.target.value)} placeholder="Role" />
      <button type="submit" disabled={creating || updating}>
        {user ? 'Update' : 'Submit'}
      </button>
    </form>
  );
};

export default UserForm;
