import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { ApolloProviderWrapper } from './apolloClient';
import UserList from './components/UserList';
import HotelList from './components/HotelList';
import RoomList from './components/RoomList';
import RoomTypeList from './components/RoomTypeList';
import BookingList from './components/BookingList';
import './App.css';

const images = [
  '/images/image1.jpg',
  '/images/image2.jpg',
  '/images/image3.jpg',
  
];

const App = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const prevImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === 0 ? images.length - 1 : prevIndex - 1));
  };

  const nextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <ApolloProviderWrapper>
      <Router>
        <div className="App">
          <nav>
            {/* Navigation Links */}
          </nav>

          <main>
            <Routes>
        <Route path="/users" element={<UserList />} />
        <Route path="/hotels" element={<HotelList />} />
        <Route path="/rooms" element={<RoomList />} />
        <Route path="/room-types" element={<RoomTypeList />} />
        <Route path="/bookings" element={<BookingList />} />
        <Route path="/" element={<div className="WelcomeSection"><h1>Welcome to the Hotel Booking System</h1></div>} />
            </Routes>
          </main>

          <div className="WelcomeSection" style={{ backgroundImage: `url(${images[currentImageIndex]})` }}>
            <button onClick={prevImage}>&lt;</button>
            <h1>Welcome to the Hotel Booking System</h1>
            <button onClick={nextImage}>&gt;</button>
          </div>
        </div>
      </Router>
    </ApolloProviderWrapper>
  );
};

export default App;
