import React from 'react';
import { useQuery, gql } from '@apollo/client';
import RoomTypeForm from './RoomTypeForm'; 

const GET_ROOM_TYPES = gql`
  query GetRoomTypes {
    roomTypes {
      id
      name
      description
      price
    }
  }
`;

const RoomTypeList = () => {
  const { loading, error, data, refetch } = useQuery(GET_ROOM_TYPES);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <div>
      <h2>Room Type List</h2>
      <RoomTypeForm refetchRoomTypes={refetch} /> {/* Include RoomTypeForm component and pass refetch */}
      <ul>
        {data.roomTypes.map(({ id, name, description, price }) => (
          <li key={id}>
            <div>
              <h3>{name}</h3>
              <p>{description}</p>
              <p>Price: {price}</p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RoomTypeList;
