import React, { useState, useEffect } from 'react';
import { useMutation } from '@apollo/client';
import { CREATE_HOTEL, UPDATE_HOTEL } from '../graphql/mutations';

const HotelForm = ({ hotel, refetchHotels }) => {
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [description, setDescription] = useState('');

  useEffect(() => {
    if (hotel) {
      setName(hotel.name);
      setAddress(hotel.address);
      setDescription(hotel.description);
    } else {
      setName('');
      setAddress('');
      setDescription('');
    }
  }, [hotel]);

  const [createHotel, { loading: creating }] = useMutation(CREATE_HOTEL, {
    onCompleted: () => {
      console.log('Hotel created successfully');
      setName('');
      setAddress('');
      setDescription('');
      refetchHotels();
    },
    onError: (error) => {
      console.error('Error creating hotel:', error.message);
    },
  });

  const [updateHotel, { loading: updating }] = useMutation(UPDATE_HOTEL, {
    onCompleted: () => {
      console.log('Hotel updated successfully');
      refetchHotels();
    },
    onError: (error) => {
      console.error('Error updating hotel:', error.message);
    },
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (hotel) {
      updateHotel({ variables: { id: hotel.id, input: { name, address, description } } });
    } else {
      createHotel({ variables: { name, address, description } });
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Hotel Name" />
      <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} placeholder="Address" />
      <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description" />
      <button type="submit" disabled={creating || updating}>
        {hotel ? 'Update' : 'Submit'}
      </button>
    </form>
  );
};

export default HotelForm;
