import React, { useState } from 'react';
import { useMutation } from '@apollo/client';
import { CREATE_HOTEL } from '../graphql/mutations'; 

const HotelForm = ({ refetchHotels }) => {
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [description, setDescription] = useState('');

  const [createHotel, { loading }] = useMutation(CREATE_HOTEL, {
    onCompleted: () => {
      console.log('Hotel created successfully');
      setName('');
      setAddress('');
      setDescription('');
      refetchHotels();
    },
    onError: (error) => {
      console.error('Error creating hotel:', error.message);
    },
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    createHotel({ variables: { name, address, description } });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Hotel Name" />
      <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} placeholder="Address" />
      <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description" />
      <button type="submit" disabled={loading}>Submit</button>
    </form>
  );
};

export default HotelForm;
