import React, { useState } from 'react';
import { useQuery, gql } from '@apollo/client';
import HotelForm from './HotelForm';
import DeleteButton from './DeleteButton';

const GET_HOTELS = gql`
  query GetHotels {
    hotels {
      id
      name
      address
      description
    }
  }
`;

const HotelList = () => {
  const { loading, error, data, refetch } = useQuery(GET_HOTELS);
  const [selectedHotel, setSelectedHotel] = useState(null);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  const handleEdit = (hotel) => {
    setSelectedHotel(hotel);
  };

  const handleDelete = () => {
    setSelectedHotel(null);
    refetch();
  };

  return (
    <div>
      <h2>Hotel List</h2>
      <ul>
        {data.hotels.map((hotel) => (
          <li key={hotel.id}>
            <div>
              <h3>{hotel.name}</h3>
              <p>{hotel.address}</p>
              <p>{hotel.description}</p>
            </div>
            <button onClick={() => handleEdit(hotel)}>Edit</button>
            <DeleteButton id={hotel.id} onDelete={handleDelete} />
          </li>
        ))}
      </ul>
      <h2>{selectedHotel ? 'Edit Hotel' : 'Create Hotel'}</h2>
      <HotelForm hotel={selectedHotel} refetchHotels={refetch} />
    </div>
  );
};

export default HotelList;
