import React from 'react';
import { useQuery, gql } from '@apollo/client';
import BookingForm from './BookingForm'; 

const GET_BOOKINGS = gql`
  query GetBookings {
    bookings {
      id
      userId
      roomId
      checkInDate
      checkOutDate
      status
    }
  }
`;

const BookingList = () => {
  const { loading, error, data, refetch } = useQuery(GET_BOOKINGS);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <div>
      <h2>Booking List</h2>
      <BookingForm refetchBookings={refetch} /> {/* Include BookingForm component and pass refetch */}
      <ul>
        {data.bookings.map(({ id, userId, roomId, checkInDate, checkOutDate, status }) => (
          <li key={id}>
            <div>
              <h3>Booking ID: {id}</h3>
              <p>User ID: {userId}</p>
              <p>Room ID: {roomId}</p>
              <p>Check-In Date: {checkInDate}</p>
              <p>Check-Out Date: {checkOutDate}</p>
              <p>Status: {status}</p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BookingList;
