// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { ApolloProviderWrapper } from './apolloClient';
import UserList from './components/UserList';
import HotelList from './components/HotelList';
import RoomList from './components/RoomList';
import RoomTypeList from './components/RoomTypeList';
import BookingList from './components/BookingList';


const App = () => {
  return (
    <ApolloProviderWrapper>
      <Router>
        <div>
          <nav>
            <ul>
              <li>
                <Link to="/users">Users</Link>
              </li>
              <li>
                <Link to="/hotels">Hotels</Link>
              </li>
              <li>
                <Link to="/rooms">Rooms</Link>
              </li>
              <li>
                <Link to="/room-types">Room Types</Link>
              </li>
              <li>
                <Link to="/bookings">Bookings</Link>
              </li>
            </ul>
          </nav>

          <Routes>
            <Route path="/users" element={<UserList />} />
            <Route path="/hotels" element={<HotelList />} />
            <Route path="/rooms" element={<RoomList />} />
            <Route path="/room-types" element={<RoomTypeList />} />
            <Route path="/bookings" element={<BookingList />} />
            <Route path="/" element={<h1>Welcome to the Hotel Booking System</h1>} />
          </Routes>
        </div>
      </Router>
    </ApolloProviderWrapper>
  );
};

export default App;
