import React, { useState } from 'react';
import { useMutation } from '@apollo/client';
import { CREATE_ROOM_TYPE } from '../graphql/mutations';

const RoomTypeForm = () => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');

  const [createRoomType, { loading }] = useMutation(CREATE_ROOM_TYPE, {
    onCompleted: () => {
      console.log('Room type created successfully');
      setName('');
      setDescription('');
      setPrice('');
    },
    onError: (error) => {
      console.error('Error creating room type:', error.message);
    },
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    createRoomType({ variables: { name, description, price } });
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column' }}>
      <div style={{ marginBottom: 'rem' }}>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Name" />
      </div>
      <div style={{ marginBottom: '1rem' }}>
        <textarea value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description" />
      </div>
      <div style={{ marginBottom: 'rem' }}>
        <input type="text" value={price} onChange={(e) => setPrice(e.target.value)} placeholder="Price" />
      </div>
      <div style={{ marginBottom: 'rem' }}>
        <button type="submit" disabled={loading}>Submit</button>
      </div>
    </form>
  );
};

export default RoomTypeForm;
